from pathlib import Path
from typing import List
import numpy as np
from sklearn.feature_extraction.text import TfidfVectorizer
from sklearn.decomposition import TruncatedSVD
import joblib
from sklearn.pipeline import Pipeline
import nltk


class LSA:
    """
    Latent Semantic Analysis Encoder.

    Parameters:
    -----------
    verbose : bool, optional (default=True)
        Whether to print progress messages during fitting.

    Attributes:
    -----------
    verbose : bool (default=True)
        Whether to print progress messages during fitting.
    model : TruncatedSVD or None
        The fitted TruncatedSVD model.
    tokenizer : Tokenizer
        The tokenizer used to preprocess text.
    tfidf : TfidfVectorizer
        The TfidfVectorizer used to transform text into a TF-IDF matrix.
    """

    def __init__(self, verbose: bool = True) -> None:
        self.verbose = verbose
        self.model = None
        self.tokenizer = Tokenizer()
        self.tfidf = TfidfVectorizer(
            strip_accents="unicode",
            analyzer="word",
            tokenizer=self.tokenizer,
            stop_words="english",
            max_df=0.95,
            min_df=1/10000,
            dtype=np.float32
        )

    def encode(self, text_list: List[str]):
        """Encode a list of assay descriptions using a fitted Model.

        Parameters
        ----------
        text_list : list of str
            List of assay descriptions.
        lsa_path : str
            Path to a fitted, joblib-ed sklearn model.

        Returns
        -------
        numpy.ndarray
            Numpy dense matrix with shape (n_assays, n_components).
        """
        return self.model.transform(text_list)

    def fit(self, text_list: List[str], n_components: int = 355):
        """Fit a sklearn TruncatedSVD model using a list of assay descriptions.

        Parameters
        ----------
        text_list : list of str
            List of assay descriptions.
        lsa_save_path : str
            Path to where the fitted model will be saved.
        n_components : int
            Number of components to use for the TruncatedSVD model.
        """
        features = self.tfidf.fit_transform(text_list)

        if n_components > features.shape[1]:
            if self.verbose:
                print(f"Warning: n_components > n_features. Setting n_components to {features.shape[1]}.")
            n_components = features.shape[1]

        svd = TruncatedSVD(n_components=n_components)
        svd.fit(features)

        self.model = Pipeline([("tfidf", self.tfidf), ("svd", svd)])

    def save(self, lsa_save_path):
        """Save a fitted sklearn TruncatedSVD model.

        Parameters
        ----------
        lsa_save_path : str
            Path to where the fitted model will be saved.
        """
        lsa_save_path = Path(lsa_save_path)
        lsa_save_path.parent.mkdir(parents=True, exist_ok=True)
        joblib.dump(self.model, lsa_save_path)

    def load(self, lsa_load_path):
        """Load a fitted sklearn TruncatedSVD model.

        Parameters
        ----------
        lsa_path : str
            Path to where the fitted model is saved.
        """
        self.model = joblib.load(lsa_load_path)


class Tokenizer:

    def __init__(self):
       
        self.nltk = nltk
        # check if resources are available
        try:
            nltk.word_tokenize("Hello world.")
        except LookupError:
            nltk.download("punkt")

        try:
            from nltk.corpus import wordnet
            self.wordnet = wordnet
            wordnet.ADJ
            # check punkt
            self.nltk.word_tokenize("Hello world.")
        except LookupError:
            nltk.download("wordnet")
            nltk.download("punkt")
            nltk.download("averaged_perceptron_tagger")
            from nltk.corpus import wordnet
            self.wordnet = wordnet

        self.lemmatizer = nltk.stem.WordNetLemmatizer()
        self.tag2pos = {
            "J": self.wordnet.ADJ,
            "N": self.wordnet.NOUN,
            "V": self.wordnet.VERB,
            "R": self.wordnet.ADV
        }

    def _get_wordnet_pos(self, word):
        tag = self.nltk.pos_tag([word])[0][1][0].upper()
        return self.tag2pos.get(tag, self.wordnet.NOUN)

    def __call__(self, doc):
        for word in self.nltk.word_tokenize(doc):
            pos = self._get_wordnet_pos(word)
            yield self.lemmatizer.lemmatize(word, pos)

